mapper.select:
SELECT B.*, BG.*, (SELECT EMP_NAME FROM EMPLOYEE WHERE EMP_NO = B.EMP_NO) WRITERNAME
FROM BOARD B, BOARDGROUP BG
WHERE B.BO_NO = #{bo_no}
AND B.BO_CODE = #{bo_code}
AND B.BO_CODE = BG.BG_CODE

mapper.update:
UPDATE BOARD 
SET BO_CONTENT = #{bo_content}
WHERE BO_NO = #{bo_no}
AND BO_STATUS='y'

mapper.delete:
UPDATE BOARD SET BO_STATUS='n'
WHERE BO_NO = #{bo_no}
AND BO_CODE = #{bo_code}
